SELECT p.first_name, p.last_name, CASE WHEN pf.H = 0 THEN NULL ELSE s.salary / pf.H END AS "dollars per hit" FROM players p JOIN performances pf ON p.id = pf.player_id JOIN salaries s ON p.id = s.player_id AND pf.year = s.year WHERE pf.year = 2001 AND pf.H > 0 ORDER BY "dollars per hit" ASC NULLS LAST, p.first_name ASC, p.last_name ASC LIMIT 10;
