SELECT p.first_name, p.last_name, CASE WHEN perf.H > 0 THEN ROUND(s.salary / perf.H, 2) ELSE NULL END AS "dollars per hit" FROM players p JOIN (SELECT player_id, H FROM performances WHERE year = 2001 AND H > 0) perf ON p.id = perf.player_id JOIN (SELECT player_id, salary FROM salaries WHERE year = 2001) s ON p.id = s.player_id ORDER BY "dollars per hit", p.first_name, p.last_name LIMIT 10;
